<?phpnamespace App\Command\Garmin;use App\Command\AbstractCommand;use App\Service\GarminActivityDetailsManager;use Symfony\Component\Console\Input\InputArgument;class ImportActivityDetailsCommand extends AbstractCommand{    protected static $defaultName = 'garmin:activity:import';    protected $title = 'Import garmin activity details';    private $garminManager;    public function __construct(GarminActivityDetailsManager $garminManager)    {        $this->garminManager = $garminManager;        parent::__construct();    }    protected function configure()    {        $this            ->setDescription('Imports garmin activity details.')            ->setHelp('')            ->addArgument('id', InputArgument::REQUIRED, 'Activity ID');;    }    protected function handle(): void    {        $this->garminManager->setActivityId($this->input->getArgument('id'));        $this->garminManager->import();        $this->info('ok');    }}